import React, { useState } from "react";
import './CampInfoObject.css';
// import { useNavigate } from "react-router-dom";
import SiteComponent from 'src/Compnent/SiteComponent/SiteComponent'; // SiteComponent 컴포넌트를 불러옵니다

const CampInfoObject = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const components = [<SiteComponent />, <SiteComponent />, <SiteComponent />, <SiteComponent />];

    // const navigate = useNavigate();

    // const navigateToCampPage = () => {
    //     navigate("/camppage");
    // };

    const handlePrevClick = (e) => {
        e.stopPropagation();
        setCurrentIndex((prevIndex) => (prevIndex - 1 + components.length) % components.length);
    };

    const handleNextClick = (e) => {
        e.stopPropagation();
        setCurrentIndex((prevIndex) => (prevIndex + 1) % components.length);
    };

    return (
        <div className={"camp-scroll-box"}>
            <div className={"carousel-container"}>
                {components.map((component, index) => (
                    <div key={index} className={`carousel-item ${index === currentIndex ? "active" : ""}`}>
                        {component}
                    </div>
                ))}
                <div className="carousel-buttons">
                    <button className="carousel-button" onClick={handlePrevClick}>‹</button>
                    <button className="carousel-button" onClick={handleNextClick}>›</button>
                </div>
            </div>
        </div>
    );
}

export default CampInfoObject;
