import React, { useState } from 'react';
import './MainPage.css';
import { useHistory } from 'react-router-dom';
import CampInfoObject from "../Compnent/CampInfoObject/CampInfoObject";
import CampFilter from "../CampFilter/CampFilter";

const MainPage = ({ props }) => {
    const [showModal, setShowModal] = useState(false);
    const history = useHistory();

    const handleFilterButtonClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    // CampFilter로부터 값을 받아오는 함수
    const handleFilterApply = (region, checkInDate, checkOutDate) => {
        // 값들을 URL에 추가하여 다른 페이지로 이동
        const queryParams = `?region=${region}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`;
        history.push(`/another-page${queryParams}`);
    };

    return (
        <div className="container">
            <div className="MainFrame">
                <div className={"main-mypage-button"}></div>
                <div className={"main-filter-button"} onClick={handleFilterButtonClick}>검색필터</div>
                <div className={"main-row-line"}></div>
                <div className={"scroll-box"}>
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                    <CampInfoObject />
                </div>
            </div>
            {/* CampFilter에 필터 적용 함수를 props로 전달 */}
            <CampFilter show={showModal} onClose={handleCloseModal} onApplyFilter={handleFilterApply} />
        </div>
    );
}

export default MainPage;
