import React, { useState } from "react";
import './SiteComponent.css';
import { useNavigate } from "react-router-dom";
import Calendar from 'react-calendar'; // react-calendar import

const SiteComponent = ({ props }) => {
    const navigate = useNavigate();
    const [currentIndex, setCurrentIndex] = useState(0);
    const [showModal, setShowModal] = useState(false); // 모달 표시 여부 상태

    const navigateToCampPage = () => {
        setShowModal(true); // 클릭 시 모달 표시
    };

    const nextSite = () => {
        setCurrentIndex((prevIndex) => (prevIndex === sites.length - 1 ? 0 : prevIndex + 1));
    };

    const prevSite = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? sites.length - 1 : prevIndex - 1));
    };

    const closeModal = () => {
        setShowModal(false); // 모달 닫기
    };


    const sites = ["사이트타입 1", "사이트타입 2", "사이트타입 3"];


    const [currentImage, setCurrentImage] = useState(0);
    const [selectedDate, setSelectedDate] = useState(null);
    const images = [
        { src: '사진1.jpg', date: new Date(2024, 5, 6) }, // 빨간색으로 표시할 날짜를 지정
        { src: '사진2.jpg', date: new Date(2024, 5, 7) },
        { src: '사진3.jpg', date: new Date(2024, 5, 8) }
    ];

    const handlePrev = () => {
        setCurrentImage((prev) => (prev === 0 ? images.length - 1 : prev - 1));
    };

    const handleNext = () => {
        setCurrentImage((prev) => (prev === images.length - 1 ? 0 : prev + 1));
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const customTileContent = ({ date, view }) => {
        // 특정 날짜가 선택되었고, 해당 날짜에 이미지가 있는 경우 빨간색으로 표시
        if (view === 'month' && images.find(img => img.date.getTime() === date.getTime())) {
            return <div style={{ color: 'red' }}>•</div>;
        }
    };

    return (
        <div className={"site-carousel"}>
            <button onClick={prevSite}>이전</button>
            <div className={"site-list-box"} onClick={navigateToCampPage}>
                <div className={"site-type"} >
                    {sites[currentIndex]}
                </div>
            </div>
            <button onClick={nextSite}>다음</button>

            {/* 모달 */}
            {showModal && (
                <div className="modal" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={closeModal}>&times;</span>

                        {/* 카루셀 */}
                        <div className="carousel-container">
                            <button onClick={handlePrev}>이전</button>
                            <img src={images[currentImage].src} alt={`사진 ${currentImage + 1}`}/>
                            <button onClick={handleNext}>다음</button>
                        </div>

                        {/* 수용인원 표시 */}
                        <p>수용인원: 10명</p>

                        {/* 예약 현황 달력 */}
                        <Calendar onChange={handleDateChange} value={selectedDate} tileContent={customTileContent}/>

                        {/* 예약하러 가기 버튼 */}
                        <button>예약하러 가기</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default SiteComponent;
