import React, { useState } from "react";
import './SiteComponent.css';
import { useNavigate } from "react-router-dom";

const SiteComponent = ({ props }) => {
    const navigate = useNavigate();
    const [currentIndex, setCurrentIndex] = useState(0);
    const [showModal, setShowModal] = useState(false); // 모달 표시 여부 상태

    const navigateToCampPage = () => {
        setShowModal(true); // 클릭 시 모달 표시
    };

    const nextSite = () => {
        setCurrentIndex((prevIndex) => (prevIndex === sites.length - 1 ? 0 : prevIndex + 1));
    };

    const prevSite = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? sites.length - 1 : prevIndex - 1));
    };

    const closeModal = () => {
        setShowModal(false); // 모달 닫기
    };

    const sites = ["사이트타입 1", "사이트타입 2", "사이트타입 3"];

    return (
        <div className={"site-carousel"}>
            <button onClick={prevSite}>이전</button>
            <div className={"site-list-box"} onClick={navigateToCampPage}>
                <div className={"site-type"} >
                    {sites[currentIndex]}
                </div>
            </div>
            <button onClick={nextSite}>다음</button>

            {/* 모달 */}
            {showModal && (
                <div className="modal" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close" onClick={closeModal}>&times;</span>
                        {/* 카루셀 부분 */}
                        <Carousel>
                            <Carousel.Item>
                                <img
                                    className="d-block w-100"
                                    src="사진1.jpg"
                                    alt="First slide"
                                />
                                <Carousel.Caption>
                                    <h3>카루셀 제목 1</h3>
                                    <p>카루셀 내용 1</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                            <Carousel.Item>
                                <img
                                    className="d-block w-100"
                                    src="사진2.jpg"
                                    alt="Second slide"
                                />
                                <Carousel.Caption>
                                    <h3>카루셀 제목 2</h3>
                                    <p>카루셀 내용 2</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                            <Carousel.Item>
                                <img
                                    className="d-block w-100"
                                    src="사진3.jpg"
                                    alt="Third slide"
                                />
                                <Carousel.Caption>
                                    <h3>카루셀 제목 3</h3>
                                    <p>카루셀 내용 3</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        </Carousel>

                        {/* 수용인원 표시 부분 */}
                        <p>수용인원: 10명</p>

                        {/* 예약 현황 달력 */}
                        <div>
                            <p>예약 현황 달력</p>
                            {/* 예약 현황 달력 컴포넌트를 넣는 부분 */}
                        </div>

                        {/* 예약하러 가기 버튼 */}
                        <button>예약하러 가기</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default SiteComponent;
