import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // React Router의 useHistory를 가져옴
import './CampFilter.css';

const CampFilter = ({ show, onClose }) => {
    const [region, setRegion] = useState('');
    const [checkInDate, setCheckInDate] = useState('');
    const [checkOutDate, setCheckOutDate] = useState('');
    const history = useHistory();

    if (!show) {
        return null;
    }

    const handleConfirm = () => {
        // 값 확인 후 URL에 전달하고 다른 페이지로 이동
        const queryParams = `?region=${region}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`;
        history.push(`/MainPage${queryParams}`);
    }

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <button onClick={onClose}>Close</button>
                <div className="modal-body">
                    <div className="checkbox-container">
                        <label><input type="checkbox" /> 캠핑</label>
                        <label><input type="checkbox" /> 글램핑</label>
                        <label><input type="checkbox" /> 카라반</label>
                        <label><input type="checkbox" /> 펜션</label>
                    </div>
                    <div className="input-container">
                        <label>지역</label>
                        <input type="text" placeholder="입력 1" value={region} onChange={(e) => setRegion(e.target.value)} />
                        <label>입실일</label>
                        <input type="date" placeholder="입력 2" value={checkInDate} onChange={(e) => setCheckInDate(e.target.value)} />
                        <label>퇴실일</label>
                        <input type="date" placeholder="입력 3" value={checkOutDate} onChange={(e) => setCheckOutDate(e.target.value)} />
                    </div>
                    <button onClick={handleConfirm}>확인</button>
                </div>
            </div>
        </div>
    );
}

export default CampFilter;
